use mas_iana::jose::{
    JsonWebKeyEcEllipticCurve, JsonWebKeyOperation, JsonWebKeyType, JsonWebKeyUse,
    JsonWebSignatureAlg,
};
use schemars::JsonSchema;
use serde::{Deserialize, Serialize};
use serde_with::{
    base64::{Base64, Standard, UrlSafe},
    formats::{Padded, Unpadded},
    serde_as, skip_serializing_none,
};
use url::Url;

use crate::constraints::Constrainable;

pub(crate) mod private_parameters;
pub(crate) mod public_parameters;

pub use self::public_parameters::JsonWebKeyPublicParameters as JsonWebKeyParameters;

trait JwkEcCurve {
    const CRV: JsonWebKeyEcEllipticCurve;
}

impl JwkEcCurve for p256::NistP256 {
    const CRV: JsonWebKeyEcEllipticCurve = JsonWebKeyEcEllipticCurve::P256;
}

impl JwkEcCurve for p384::NistP384 {
    const CRV: JsonWebKeyEcEllipticCurve = JsonWebKeyEcEllipticCurve::P384;
}

impl JwkEcCurve for k256::Secp256k1 {
    const CRV: JsonWebKeyEcEllipticCurve = JsonWebKeyEcEllipticCurve::Secp256K1;
}

#[serde_as]
#[skip_serializing_none]
#[derive(Debug, Clone, PartialEq, Eq, Serialize, Deserialize, JsonSchema)]
pub struct JsonWebKey {
    #[serde(flatten)]
    parameters: JsonWebKeyParameters,

    #[serde(default)]
    r#use: Option<JsonWebKeyUse>,

    #[serde(default)]
    key_ops: Option<Vec<JsonWebKeyOperation>>,

    #[serde(default)]
    alg: Option<JsonWebSignatureAlg>,

    #[serde(default)]
    kid: Option<String>,

    #[schemars(with = "Option<String>")]
    #[serde(default)]
    x5u: Option<Url>,

    #[schemars(with = "Vec<String>")]
    #[serde(default)]
    #[serde_as(as = "Option<Vec<Base64<Standard, Padded>>>")]
    x5c: Option<Vec<Vec<u8>>>,

    #[schemars(with = "Option<String>")]
    #[serde(default)]
    #[serde_as(as = "Option<Base64<UrlSafe, Unpadded>>")]
    x5t: Option<Vec<u8>>,

    #[schemars(with = "Option<String>")]
    #[serde(default, rename = "x5t#S256")]
    #[serde_as(as = "Option<Base64<UrlSafe, Unpadded>>")]
    x5t_s256: Option<Vec<u8>>,
}

impl JsonWebKey {
    #[must_use]
    pub const fn new(parameters: JsonWebKeyParameters) -> Self {
        Self {
            parameters,
            r#use: None,
            key_ops: None,
            alg: None,
            kid: None,
            x5u: None,
            x5c: None,
            x5t: None,
            x5t_s256: None,
        }
    }

    #[must_use]
    pub const fn with_use(mut self, value: JsonWebKeyUse) -> Self {
        self.r#use = Some(value);
        self
    }

    #[must_use]
    pub fn with_key_ops(mut self, key_ops: Vec<JsonWebKeyOperation>) -> Self {
        self.key_ops = Some(key_ops);
        self
    }

    #[must_use]
    pub const fn with_alg(mut self, alg: JsonWebSignatureAlg) -> Self {
        self.alg = Some(alg);
        self
    }

    #[must_use]
    pub fn with_kid(mut self, kid: impl Into<String>) -> Self {
        self.kid = Some(kid.into());
        self
    }

    #[must_use]
    pub const fn alg(&self) -> Option<JsonWebSignatureAlg> {
        self.alg
    }

    #[must_use]
    pub const fn params(&self) -> &JsonWebKeyParameters {
        &self.parameters
    }
}

impl Constrainable for JsonWebKey {
    fn kid(&self) -> Option<&str> {
        self.kid.as_deref()
    }

    fn kty(&self) -> JsonWebKeyType {
        match self.parameters {
            JsonWebKeyParameters::Ec { .. } => JsonWebKeyType::Ec,
            JsonWebKeyParameters::Rsa { .. } => JsonWebKeyType::Rsa,
            JsonWebKeyParameters::Okp { .. } => JsonWebKeyType::Okp,
        }
    }

    fn algs(&self) -> Option<Vec<JsonWebSignatureAlg>> {
        if let Some(alg) = self.alg {
            Some(vec![alg])
        } else {
            match &self.parameters {
                JsonWebKeyParameters::Rsa { .. } => Some(vec![
                    JsonWebSignatureAlg::Rs256,
                    JsonWebSignatureAlg::Rs384,
                    JsonWebSignatureAlg::Rs512,
                    JsonWebSignatureAlg::Ps256,
                    JsonWebSignatureAlg::Ps384,
                    JsonWebSignatureAlg::Ps512,
                ]),
                JsonWebKeyParameters::Ec(params) => match params.crv() {
                    JsonWebKeyEcEllipticCurve::P256 => Some(vec![JsonWebSignatureAlg::Es256]),
                    JsonWebKeyEcEllipticCurve::P384 => Some(vec![JsonWebSignatureAlg::Es384]),
                    JsonWebKeyEcEllipticCurve::P521 => Some(vec![JsonWebSignatureAlg::Es512]),
                    JsonWebKeyEcEllipticCurve::Secp256K1 => Some(vec![JsonWebSignatureAlg::Es256K]),
                },
                JsonWebKeyParameters::Okp { .. } => Some(vec![JsonWebSignatureAlg::EdDsa]),
            }
        }
    }

    fn use_(&self) -> Option<JsonWebKeyUse> {
        self.r#use
    }
}

#[derive(Debug, Clone, PartialEq, Eq, Serialize, Deserialize, JsonSchema)]
pub struct JsonWebKeySet {
    keys: Vec<JsonWebKey>,
}

impl std::ops::Deref for JsonWebKeySet {
    type Target = Vec<JsonWebKey>;

    fn deref(&self) -> &Self::Target {
        &self.keys
    }
}

impl JsonWebKeySet {
    #[must_use]
    pub fn new(keys: Vec<JsonWebKey>) -> Self {
        Self { keys }
    }
}

